Data Flow Diagram.code:
digraph integration_flow {
    rankdir=LR;
    node [shape=box, style=rounded];

    // Systems
    Shoppers_Stop [label="Shoppers Stop", shape=ellipse, style=filled, fillcolor=lightblue];
    Darwinbox [label="Darwinbox", shape=ellipse, style=filled, fillcolor=lightgreen];

    // Integration Points
    API_Gateway [label="API Gateway", shape=circle, style=filled, fillcolor=yellow];
    Integration_Point [label="Integration Point", shape=diamond, style=filled, fillcolor=pink];

    // Data Processing Steps
    Data_Validation [label="Data Validation", shape=octagon, style=filled, fillcolor=orange];
    Data_Transformation [label="Data Transformation", shape=octagon, style=filled, fillcolor=orange];
    Custom_Logic [label="Custom Logic", shape=octagon, style=filled, fillcolor=orange];

    // Data Flow
    Darwinbox ->> API_Gateway [label="Attendance Punches", style=dashed, color=gray];
    API_Gateway ->> Integration_Point [label="API Call", style=solid, color=black];
    Integration_Point ->> Data_Validation [label="Raw Data", style=solid, color=black];
    Data_Validation ->> Data_Transformation [label="Validated Data", style=solid, color=black];
    Data_Transformation ->> Custom_Logic [label="Transformed Data", style=solid, color=black];
    Custom_Logic ->> Shoppers_Stop [label="Processed Data", style=solid, color=black];

    // Rank Ordering
    {rank=same; Darwinbox; API_Gateway;}
    {rank=same; Integration_Point; Data_Validation;}
    {rank=same; Data_Transformation; Custom_Logic;}
    {rank=same; Shoppers_Stop;}
}
