Data Flow Diagram.code:
digraph integration_flow {
    rankdir=LR;
    node [shape=box, style=rounded];

    // Systems
    Shoppers_Stop [label="Shoppers Stop", fillcolor="#ADD8E6"];
    Darwinbox [label="Darwinbox", fillcolor="#ADD8E6"];

    // Integration Points
    API_Gateway [label="API Gateway", shape=hexagon, fillcolor="#CCCCCC"];
    Data_Receiver [label="Data Receiver", shape=circle, fillcolor="#CCCCCC"];

    // Data Processing Steps
    Data_Transformation [label="Data Transformation", shape=ellipse, fillcolor="#FFFFCC"];
    Data_Validation [label="Data Validation", shape=ellipse, fillcolor="#FFFFCC"];
    Custom_Logic [label="Custom Logic", shape=octagon, fillcolor="#FFFFCC"];

    // Data Flow
    Darwinbox ->> API_Gateway [label="Attendance Punches", dir=both, arrowtail=empty];
    API_Gateway ->> Data_Receiver [label="API Call", dir=both, arrowtail=empty];
    Data_Receiver ->> Data_Transformation [label="Raw Data", dir=forward, arrowtail=empty];
    Data_Transformation ->> Data_Validation [label="Transformed Data", dir=forward, arrowtail=empty];
    Data_Validation ->> Custom_Logic [label="Validated Data", dir=forward, arrowtail=empty];
    Custom_Logic ->> Shoppers_Stop [label="Processed Data", dir=forward, arrowtail=empty];

    // Rank Ordering
    {rank=same; Darwinbox; API_Gateway;}
    {rank=same; Data_Receiver; Data_Transformation;}
    {rank=same; Data_Validation; Custom_Logic;}
    {rank=same; Shoppers_Stop;}
}
