Data Flow Diagram.code:
digraph apibased_integration {
    rankdir=LR;
    node [shape=box, style="rounded,filled", fontname="Arial"];
    edge [fontname="Arial"];

    shoppersstop [label="Shoppers Stop\nSystem", fillcolor=lightblue];
    api [label="Integration\nLayer", fillcolor=lightgreen];
    auth [label="Authentication\nService", fillcolor=yellow];
    darwinbox [label="darwinbox\nSystem", fillcolor=lightcoral];

    darwinbox -> api [label="Request"];
           api -> auth [label="Authenticate"];
           auth -> api [label="Validated"];
           api -> shoppersstop [label="Fetch Data"];
           shoppersstop -> api [label="Data"];
           api -> darwinbox [label="Response"];
}
