Data Flow Diagram.code:
digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_visteon {
        label = "Visteon";
        style = filled;
        color = lightblue;

        hr_term_bot [label="HR Term Bot", shape=cylinder];
    }

    subgraph cluster_darwinbox {
        label = "Darwinbox Digital Solutions";
        style = filled;
        color = lightgreen;

        darwinbox [label="Darwinbox\n(Source System)", shape=cylinder];
        api_gateway [label="API Gateway", shape=box];
        authentication [label="Authentication\n(OAuth/API Key)", shape=diamond];
        data_extraction [label="Data Extraction\n(Active/Inactive Employees)", shape=box];
        business_logic [label="Sync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees via outbound integration using SFTP with encryption and predefined file formats.", shape=box];
        attendance_module [label="Attendance Module", shape=component];
        data_transformation [label="Data Transformation\n(Predefined Format)", shape=box];
        sftp_server [label="SFTP Server\n(Encrypted)", shape=cylinder];
    }

    darwinbox -> api_gateway [label="Data Request"];
    api_gateway -> authentication [label="Authenticate"];
    authentication -> data_extraction [label="Access Granted"];
    data_extraction -> business_logic [label="Employee Data"];
    business_logic -> attendance_module [label="Attendance Data"];
    business_logic -> data_transformation [label="Filtered Employee Data"];
    data_transformation -> sftp_server [label="Transformed Data"];
    sftp_server -> hr_term_bot [label="SFTP Transfer\n(to transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon.)"];

    // Styling
    api_gateway [style=filled, fillcolor=yellow];
    authentication [style=filled, fillcolor=orange];
    data_extraction [style=filled, fillcolor=lightgray];
    business_logic [style=filled, fillcolor=lightgray];
    data_transformation [style=filled, fillcolor=lightgray];
    sftp_server [style=filled, fillcolor=lightgray];
}
