Data Flow Diagram.code:
digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_visteon {
        label = "Visteon";
        style = filled;
        color = lightblue;

        hr_term_bot [label="HR Term Bot", shape=cylinder];
    }

    subgraph cluster_darwinbox {
        label = "Darwinbox Digital Solutions";
        style = filled;
        color = lightgreen;

        darwinbox [label="Darwinbox", shape=cylinder];
        api_gateway [label="API Gateway", shape=box];
        authentication [label="Authentication", shape=diamond];
        data_extraction [label="Data Extraction\n(Attendance Module)", shape=box];
        data_transformation [label="Data Transformation\n(Predefined File Formats)", shape=box];
        sftp_encryption [label="SFTP Encryption", shape=box];
    }

    darwinbox -> api_gateway [label="API Request"];
    api_gateway -> authentication [label="Authenticate"];
    authentication -> data_extraction [label="Authorization Granted"];
    data_extraction -> data_transformation [label="Attendance Data"];
    data_transformation -> sftp_encryption [label="Transformed Data"];
    sftp_encryption -> hr_term_bot [label="SFTP Transfer\n(Encrypted)"];

    data_extraction [label="Data Extraction\n(Attendance Module)\nSync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees", shape=box];
    hr_term_bot [label="HR Term Bot\nto transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon.", shape=cylinder];
}
