Data Flow Diagram.code:
digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_visteon {
        label = "Visteon";
        style = filled;
        color = lightblue;

        hr_term_bot [label="HR Term Bot", shape=cylinder];
    }

    subgraph cluster_darwinbox {
        label = "Darwinbox Digital Solutions";
        style = filled;
        color = lightgreen;

        darwinbox [label="Darwinbox", shape=cylinder];
        api_gateway [label="API Gateway", shape=box];
        authentication [label="Authentication", shape=diamond];
        data_extraction [label="Data Extraction\n(to transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon.)", shape=box];
        inactive_employee_filter [label="Inactive Employee Filter\n(date of exit ≥ 1 Jan 2020)", shape=box];
        active_employee_selection [label="Active Employee Selection", shape=box];
        data_formatting [label="Data Formatting\n(predefined file formats)", shape=box];
        sftp_encryption [label="SFTP Encryption", shape=box];
        attendance_module [label="Attendance Module", shape=component];
    }

    darwinbox -> api_gateway [label="API Request"];
    api_gateway -> authentication [label="Authenticate"];
    authentication -> data_extraction [label="Authorization"];
    data_extraction -> inactive_employee_filter [label="Employee Data"];
    data_extraction -> active_employee_selection [label="Employee Data"];
    inactive_employee_filter -> data_formatting [label="Inactive Employees"];
    active_employee_selection -> data_formatting [label="Active Employees"];
    data_formatting -> sftp_encryption [label="Formatted Data"];
    sftp_encryption -> hr_term_bot [label="SFTP Transfer\n(Sync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees via outbound integration using SFTP with encryption and predefined file formats.)"];
    darwinbox -> attendance_module [label="Attendance Data"];
    attendance_module -> data_extraction [label="Attendance Data"];
}
