Data Flow Diagram.code:
digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_visteon {
        label = "Visteon";
        style = filled;
        color = lightblue;

        hr_term_bot [label="HR Term Bot", shape=cylinder];
    }

    subgraph cluster_darwinbox {
        label = "Darwinbox Digital Solutions";
        style = filled;
        color = lightgreen;

        darwinbox [label="Darwinbox", shape=cylinder];
        api_gateway [label="API Gateway"];
        authentication [label="Authentication"];
        data_extraction [label="Data Extraction\n(Attendance Module)"];
        data_transformation [label="Data Transformation\n(Predefined File Formats)"];
        data_encryption [label="Data Encryption"];
        sftp_transfer [label="SFTP Transfer"];
    }

    darwinbox -> api_gateway [label="API Request"];
    api_gateway -> authentication [label="Authenticate"];
    authentication -> data_extraction [label="Access Granted"];
    data_extraction -> data_transformation [label="Employee Data\n(Active/Inactive)"];
    data_transformation -> data_encryption [label="Transformed Data"];
    data_encryption -> sftp_transfer [label="Encrypted Data"];
    sftp_transfer -> hr_term_bot [label="SFTP Transfer\n(to transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon.)"];

    data_extraction [label="Data Extraction\n(Attendance Module)\nSync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees via outbound integration using SFTP with encryption and predefined file formats."];
}
