digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_visteon {
        label = "Visteon";
        style = filled;
        color = lightblue;

        hr_term_bot [label="HR Term Bot", shape=cylinder];
    }

    subgraph cluster_darwinbox {
        label = "Darwinbox Digital Solutions";
        style = filled;
        color = lightgreen;

        darwinbox [label="Darwinbox", shape=cylinder];
        api_gateway [label="API Gateway"];
        authentication [label="Authentication"];
        data_extraction [label="Data Extraction\n(Attendance Module)"];
        data_transformation [label="Data Transformation\n(Predefined File Formats)"];
        sftp_encryption [label="SFTP Encryption"];
    }

    darwinbox -> api_gateway [label="Data Request"];
    api_gateway -> authentication [label="Authenticate"];
    authentication -> data_extraction [label="Access Granted"];
    data_extraction -> data_transformation [label="Attendance Data"];
    data_transformation -> sftp_encryption [label="Transformed Data"];
    sftp_encryption -> hr_term_bot [label="SFTP Transfer\n(Encrypted Data)"];

    data_extraction [label="Data Extraction\n(Attendance Module)\nBusiness Purpose: to transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon.\nProcessing"];
    data_transformation [label="Data Transformation\n(Predefined File Formats)\nBusiness Logic: Sync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees via outbound integration using SFTP with encryption and predefined file formats."];
    hr_term_bot [label="HR Term Bot\nBusiness Purpose: to transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon."];
}
