Data Flow Diagram.code:
digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_visteon {
        label = "Visteon";
        style = filled;
        color = lightblue;

        hr_term_bot [label="HR Term Bot", shape=cylinder];
        attendance_module [label="Attendance Module", shape=component];
    }

    subgraph cluster_darwinbox {
        label = "Darwinbox Digital Solutions";
        style = filled;
        color = lightgreen;

        darwinbox [label="Darwinbox", shape=cylinder];
        employee_data [label="Employee Data", shape=data];
    }

    employee_data -> data_extraction [label="Data Extraction"];
    data_extraction [label="Data Extraction from Darwinbox"];
    data_extraction -> data_filter [label="Data Filtering"];
    data_filter [label="Sync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees"];
    data_filter -> data_transformation [label="Data Transformation"];
    data_transformation [label="Data Transformation to Visteon Format"];
    data_transformation -> sftp_transfer [label="SFTP Transfer"];
    sftp_transfer [label="SFTP Transfer with Encryption"];
    sftp_transfer -> hr_term_bot [label="To transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon."];
    hr_term_bot -> attendance_module [label="Data Integration"];
}
