Data Flow Diagram.code:
digraph data_flow_diagram {
    rankdir=LR;
    node [shape=box, style=rounded];

    subgraph cluster_nnkjn {
        label = "nnkjn (Darwinbox)";
        style = filled;
        color = lightblue;

        darwinbox [label="Darwinbox\n(Source System)", shape=cylinder];
        extract_data [label="Extract Employee Data\n(Active & Inactive)"];
    }

    subgraph cluster_nnknk {
        label = "nnknk (HR Term Bot for Visteon)";
        style = filled;
        color = lightgreen;

        hr_term_bot [label="HR Term Bot\n(Target System)", shape=cylinder];
        load_data [label="Load Employee Data\ninto HR Term Bot"];
    }

    data_validation [label="Data Validation\n(Date of Exit >= 1 Jan 2020)"];
    data_transformation [label="Data Transformation\n(Predefined File Formats)"];
    sftp_transfer [label="SFTP Transfer\n(Encryption)"];
    attendance_module [label="Attendance Module\nProcessing"];
    business_purpose [label="To transfer deactivated and active employee master data from Darwinbox to HR Term Bot for Visteon.\nProcessing", shape=ellipse];
    business_logic [label="Sync only inactive employee records (date of exit ≥ 1 Jan 2020) and all active employees via outbound integration using SFTP with encryption and predefined file formats.", shape=rectangle];

    darwinbox -> extract_data [label="Data Extraction"];
    extract_data -> data_validation [label="Employee Data"];
    data_validation -> business_logic [label="Validated Data"];
    business_logic -> data_transformation [label="Filtered Data"];
    data_transformation -> sftp_transfer [label="Transformed Data"];
    sftp_transfer -> attendance_module [label="SFTP Transfer"];
    attendance_module -> load_data [label="Attendance Data"];
    load_data -> hr_term_bot [label="Employee Data"];
    extract_data -> business_purpose [label="Data for Business Purpose"];
    business_purpose -> hr_term_bot [label="Data to HR Term Bot"];
}
